[{"_id":"7f16e36f-b9e6-43bc-accd-70739e06be91","fileName":"Chatbot.py","code":"import random\nfrom datetime import datetime\n\ndef chatbot():\n    print(\"Chatbot: Hello! I am your friendly chatbot. Type 'quit' anytime to exit.\\n\")\n\n    # Predefined responses\n    greetings = [\"hello\", \"hi\", \"hey\", \"good morning\", \"good evening\"]\n    farewell = [\"bye\", \"goodbye\", \"see you\", \"take care\"]\n    how_are_you = [\"how are you\", \"how are you doing\"]\n    thanks = [\"thank you\", \"thanks\", \"thx\"]\n\n    jokes = [\n        \"Why don't scientists trust atoms? Because they make up everything!\",\n        \"Why did the computer go to the doctor? Because it caught a virus!\",\n        \"Why was the math book sad? Because it had too many problems.\"\n    ]\n\n    while True:\n        user_input = input(\"You: \").lower()\n\n        if user_input == \"quit\":\n            print(\"Chatbot: Goodbye! Have a great day!\")\n            break\n\n        # Greeting\n        elif any(word in user_input for word in greetings):\n            print(\"Chatbot: Hello there! How can I help you today?\")\n\n        # Farewell\n        elif any(word in user_input for word in farewell):\n            print(\"Chatbot: Goodbye! Come back soon.\")\n            break\n\n        # How are you\n        elif any(phrase in user_input for phrase in how_are_you):\n            print(\"Chatbot: I'm doing great, thank you! How about you?\")\n\n        # Name\n        elif \"your name\" in user_input:\n            print(\"Chatbot: I am Chatty, your Python chatbot.\")\n\n        # Time\n        elif \"time\" in user_input:\n            now = datetime.now().strftime(\"%H:%M:%S\")\n            print(f\"Chatbot: The current time is {now}\")\n\n        # Date\n        elif \"date\" in user_input or \"day\" in user_input:\n            today = datetime.today().strftime(\"%A, %B %d, %Y\")\n            print(f\"Chatbot: Today is {today}\")\n\n        # Thank you\n        elif any(word in user_input for word in thanks):\n            print(\"Chatbot: You're welcome!\")\n\n        # Joke\n        elif \"joke\" in user_input:\n            print(\"Chatbot:\", random.choice(jokes))\n\n        # Weather (simple mock response)\n        elif \"weather\" in user_input:\n            print(\"Chatbot: I can't check live weather yet, but I hope it's nice where you are!\")\n\n        # Help\n        elif \"help\" in user_input:\n            print(\"Chatbot: You can ask me things like 'time', 'date', 'tell me a joke', or just say hello!\")\n\n        # Fallback\n        else:\n            print(\"Chatbot: I'm not sure how to respond to that. Try asking something else!\")\n\nif __name__ == \"__main__\":\n    chatbot()"}]