[{"fileName":"(Python) Data Types.py","_id":"7e0bced9-999c-4e40-9c4f-5aeea436c544","code":"# Taking user input for different data types\n\n# String input\ngreeting = input(\"Enter a greeting message: \")\nprint(\"Full string:\", greeting)\nprint(\"First character (index 0):\", greeting[0])\n\n# Integer input\nage = int(input(\"Enter your age: \"))\nprint(\"Age:\", age)\n\n# Float input\nheight = float(input(\"Enter your height in meters: \"))\nprint(\"Height:\", height)\n\n# Boolean input (converted from string)\nis_student_input = input(\"Are you a student? (yes or no): \")\nis_student = is_student_input.lower() == \"yes\"\nprint(\"Is student:\", is_student)\n"}]