[{"fileName":"Expenses Tracker.py","_id":"7db08bff-41a5-4f65-909f-20061e16226a","code":"import datetime\n\n# Store expenses in a list of dictionaries\nexpenses = []\n\ndef add_expense():\n    try:\n        amount = float(input(\"Enter expense amount: $\"))\n        category = input(\"Enter category (e.g., Food, Transport, Bills): \")\n        date = datetime.date.today()\n\n        expense = {\"amount\": amount, \"category\": category, \"date\": date}\n        expenses.append(expense)\n\n        print(\"Expense added successfully!\\n\")\n    except ValueError:\n        print(\"Please enter a valid number for amount.\\n\")\n\ndef view_expenses():\n    if not expenses:\n        print(\"No expenses recorded yet.\\n\")\n        return\n\n    print(\"\\nYour Expenses:\")\n    print(\"-\" * 40)\n    for i, exp in enumerate(expenses, start=1):\n        print(f\"{i}. ${exp['amount']:.2f} - {exp['category']} ({exp['date']})\")\n    print(\"-\" * 40 + \"\\n\")\n\ndef view_summary():\n    if not expenses:\n        print(\"No expenses recorded yet.\\n\")\n        return\n\n    summary = {}\n    total = 0\n    for exp in expenses:\n        category = exp[\"category\"]\n        summary[category] = summary.get(category, 0) + exp[\"amount\"]\n        total += exp[\"amount\"]\n\n    print(\"\\nExpense Summary by Category:\")\n    print(\"-\" * 40)\n    for cat, amt in summary.items():\n        print(f\"{cat}: ${amt:.2f}\")\n    print(\"-\" * 40)\n    print(f\"Total Spent: ${total:.2f}\\n\")\n\ndef main():\n    while True:\n        print(\"====== Expense Tracker ======\")\n        print(\"1. Add Expense\")\n        print(\"2. View Expenses\")\n        print(\"3. View Summary\")\n        print(\"4. Quit\")\n        choice = input(\"Choose an option (1-4): \")\n\n        if choice == \"1\":\n            add_expense()\n        elif choice == \"2\":\n            view_expenses()\n        elif choice == \"3\":\n            view_summary()\n        elif choice == \"4\":\n            print(\"Goodbye! Stay mindful of your spending!\")\n            break\n        else:\n            print(\"Invalid choice. Try again.\\n\")\n\nif __name__ == \"__main__\":\n    main()"}]