[{"fileName":"python strings.py","_id":"fe91d7d9-a213-40f6-9609-794cee1a6f38","code":"# Basic String Operations in Python\n\n# 1. Creating Strings\ns1 = \"Hello\"\ns2 = \"World\"\n\n# 2. Concatenation\nconcat = s1 + \" \" + s2\nprint(\"Concatenation:\", concat)\n\n# 3. Repetition\nrepeat = s1 * 3\nprint(\"Repetition:\", repeat)\n\n# 4. Indexing and Slicing\nprint(\"First character:\", s1[0])\nprint(\"Last character:\", s1[-1])\nprint(\"Substring (0 to 3):\", s1[0:3])\n\n# 5. Length of String\nprint(\"Length of s1:\", len(s1))\n\n# 6. Changing Case\nprint(\"Uppercase:\", s1.upper())\nprint(\"Lowercase:\", s1.lower())\nprint(\"Title case:\", \"python string operations\".title())\n\n# 8. Replace\nprint(\"Replace 'l' with 'x':\", s1.replace(\"l\", \"x\"))\n\n# 9. Splitting and Joining\nwords = \"apple,banana,cherry\".split(\",\")\nprint(\"Split:\", words)\n\njoined = \"-\".join(words)\nprint(\"Join:\", joined)\n\n# 10. Strip (remove whitespace)\nwhitespace_str = \"   hello world   \"\nprint(\"Strip:\", whitespace_str.strip())\n\n# 11. Check string type\nprint(\"Is digit?\", \"12345\".isdigit())\nprint(\"Is alpha?\", \"Hello\".isalpha())\nprint(\"Is alphanumeric?\", \"Hello123\".isalnum())\n"}]