[{"fileName":"To-Do list.py","_id":"d4feee3b-b6a9-46e4-927b-2ab5ca9f79a2","code":"# Simple To-Do List App in Python\n\ntasks = []\n\ndef show_tasks():\n    if not tasks:\n        print(\"\\nNo tasks yet!\")\n    else:\n        print(\"\\nYour To-Do List:\")\n        for i, (task, done) in enumerate(tasks, start=1):\n            status = \"✔️\" if done else \"❌\"\n            print(f\"{i}. {task} [{status}]\")\n\ndef add_task():\n    task = input(\"Enter a new task: \")\n    tasks.append([task, False])\n    print(f\"Task '{task}' added!\")\n\ndef mark_complete():\n    show_tasks()\n    try:\n        task_num = int(input(\"\\nEnter task number to mark complete: \"))\n        if 1 <= task_num <= len(tasks):\n            tasks[task_num-1][1] = True\n            print(f\"Task '{tasks[task_num-1][0]}' marked complete!\")\n        else:\n            print(\"Invalid task number!\")\n    except ValueError:\n        print(\"Please enter a valid number.\")\n\ndef remove_task():\n    show_tasks()\n    try:\n        task_num = int(input(\"\\nEnter task number to remove: \"))\n        if 1 <= task_num <= len(tasks):\n            removed = tasks.pop(task_num-1)\n            print(f\"Task '{removed[0]}' removed!\")\n        else:\n            print(\"Invalid task number!\")\n    except ValueError:\n        print(\"Please enter a valid number.\")\n\n# Main loop\nwhile True:\n    print(\"\\n--- To-Do List Menu ---\")\n    print(\"1. Show tasks\")\n    print(\"2. Add task\")\n    print(\"3. Mark task complete\")\n    print(\"4. Remove task\")\n    print(\"5. Exit\")\n\n    choice = input(\"Choose an option (1-5): \")\n\n    if choice == \"1\":\n        show_tasks()\n    elif choice == \"2\":\n        add_task()\n    elif choice == \"3\":\n        mark_complete()\n    elif choice == \"4\":\n        remove_task()\n    elif choice == \"5\":\n        print(\"Exiting To-Do List. Goodbye!\")\n        break\n    else:\n        print(\"Invalid choice, please select 1-5.\")"}]